class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comment-form`),this.createComment(e);let t=this;$(" .delete-comment-btn",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){console.log(n.data);let o=t.newCommentDom(n.data.comment);$(`#post-${e}-comments`).prepend(o),t.deleteComment($(" .delete-comment-btn",o)),new ToggleLike($(" .toggle-like-btn",o)),new Noty({theme:"relax",text:"Comment publihed!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`\n        <div class="comment" id="comment-${e._id}">\n            <p>\n                ${e.content}\n            </p>\n            <small>\n                <a class="delete-comment-btn" href="/comments/destroy/${e._id}">X</a>\n            </small>\n            <p class="user"><i class="fas fa-user"></i>\n                ${e.user.name}\n            </p>\n            <p class="likes">\n                <a class="toggle-like-btn" href="/likes/toggle/?id=${e._id}&type=Comment" data-likes="0">0 likes</a>\n            </p>\n        </div>\n        `)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:e.prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}